/**
 * Rails-style RESTful delete method.
 * Example: 
 *                       <a href="resource/1" rel="delete" onclick="return false;">Delete</a>
 * Without confirmation: <a href="resource/1" rel="delete-now" onclick="return false;">Delete</a>
 *
 *   $('a[rel='delete']').restnoodel({ask: true});
 *   $('a[rel='delete-now']').restnoodel({ask: false});
 *
 * Compressed using Dean Edwards' Packer (http://dean.edwards.name/packer/)
 */

$.fn.restnoodel=function(a){var b={question:'Are you sure?',ask:true};this.each(function(){var o=$.extend(b,a);$(this).click(function(){if(o.ask){if(!confirm(o.question)){return}}var f=document.createElement('form');f.style.display='none';$(this).parent().append(f);f.method='POST';f.action=$(this).attr('href');var m=document.createElement('input');m.setAttribute('type','hidden');m.setAttribute('name','_method');m.setAttribute('value','delete');f.appendChild(m);f.submit()})})}